function GetSpriteState(SpriteID) 
	--SpriteID means a sprite from the chart below
	--1 = Side Left (Right must be set with facing variable)
	--3 = Side Down
	--2 = Side Up
	--4-9 = Walking
	--10-12 = Biking Idle Positions
	--13-18 = Biking
	--19-21 = Running Idle Positions
	--22-27 = Running
	--28-33 = Surfing stuff
    
    if SpriteID >= 0 and SpriteID <=9 then return 'normal' end
    if SpriteID >= 10 and SpriteID <= 18 then return 'bike' end
    if SpriteID >= 19 and SpriteID <= 31 then return 'surf_run' end --double check endpoints here
    return 'error'
end

function ResetSelfSprite(state)
    local Address = FRLGStateAddress[state][OriginalSprite+1] + 0x50
    
    for i = 1, #FRLGOriginalSprites[state][OriginalSprite+1] do 
        SetSprite(PlayerID-1, Address + FRLGOriginalSprites[state][3]*(i-1), FRLGOriginalSprites[state][OriginalSprite+1][i], FRLGPlayerPal)
    end
end

function FillSelfSprite(state, SpriteNo) 
    local Address = FRLGStateAddress[state][OriginalSprite+1] + 0x50
    for i = 1, #FRLGOriginalSprites[state][OriginalSprite+1] do 
        SetSprite(PlayerID-1, Address + FRLGOriginalSprites[state][3]*(i-1), ObjList[SpriteNo][1], ObjList[SpriteNo][2])
    end
end

